(() => {
    let out = [];

    for(let pd of document.querySelectorAll('.post-descr')){
      try{
        const a = pd.querySelector('.post-title a');
        const link = a.href;
        const title = a.title;
        var brief = "";
        for(let b of pd.querySelectorAll("p")){
          brief+=b.textContent.trim();
        }
        console.log(brief);

        out.push({
          link: link,
          title: title,
          brief: brief,
        });
      }catch(e){};
    }

    return out;
  })()

(() => {
    let out = [];

    for(let a of document.querySelectorAll('a[href^="/novosti_nauki/"]')){
      try{
        const link = a.href;
        const brief = a.querySelector(".brief").textContent;
        const title = a.querySelector(".title").textContent;
        out.push({
          link: link,
          title: title,
          brief: brief,
        });
      }catch(e){};
    }

    return out;
  })()
